mapper.update:
UPDATE file
SET original_filename = #{filename}
WHERE file_id = #{fileId}
	AND member_id = #{memberId}

mapper.select:
SELECT s3_key, original_filename
FROM file
WHERE file_id=#{fileId}
  AND member_id=#{memberId}

mapper.update:
UPDATE file
SET is_deleted = true, deleted_at = NOW()
WHERE member_id = #{memberId}
AND file_id IN

  #{id}

UPDATE file
SET is_deleted = false, deleted_at = NULL
WHERE member_id = #{memberId}
AND file_id IN

  #{id}

DELETE FROM file
WHERE member_id = #{memberId}
AND is_deleted = true
AND file_id IN

  #{id}
